function fillDownloadElements(jsonData, files) {
    for (const step of Object.values(jsonData)) {
        for (const element of Object.values(step.form.element_s)) {
            if (element.type === "download" && element.options.downloadKey) {
                const downloadKey = element.options.downloadKey;
                if (files.hasOwnProperty(downloadKey)) {
                    element.value = files[downloadKey];
                } else {
                    element.value = null;
                }
            }
        }
    }
}

