function findUploadElements(jsonData) {
    const uploadElements = {};
    for (const step of Object.values(jsonData)) {
        for (const element of Object.values(step.form.element_s)) {
            if (element.type === "upload" && element.value) {
                uploadElements[element.uploadkey] = element.value;
            }
        }
    }
    for (const step of Object.values(jsonData)) {
        for (const element of Object.values(step.form.element_s)) {
            if (element.type === "download" && element.options.downloadKey in uploadElements) {
                element.value = uploadElements[element.options.downloadKey];
            }
        }
    }
    return uploadElements;
}


